    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        Address address = new Address();
        address.customerID = Integer.parseInt (jTextField1.getText());
        address.street = jTextField10.getText();
        address.mainStreet = jTextField11.getText();
        address.district = jTextField12.getText();
        address.city = jTextField13.getText();
        address.country = jTextField14.getText();
        address.addressType = jTextField15.getText();
        
        //address.addAddress();
        DBProcesses.saveAddress(address);
    }//GEN-LAST:event_jButton1ActionPerformed

